<script>
export default {
  data() {
    return {
      name: "",
      roomCode: "default-room",
    };
  },
  created() {
    this.name = localStorage.getItem("name");
  },
  methods: {
    goToRoom() {
      localStorage.setItem("name", this.name);
      this.$router.push({
        name: "room",
        params: {
          roomCode: this.roomCode,
        },
      });
    },
  },
};
